import React, { useState, useEffect } from "react";

const UserDataForm = () => {
  // Function to generate a unique User ID
  const generateUserId = () => {
    return "USER-" + Math.floor(100000 + Math.random() * 900000); // Random 6-digit number prefixed with "USER-"
  };

  // Load saved data from localStorage (if available)
  const savedData = JSON.parse(localStorage.getItem("userData")) || {};

  // Initialize state with saved data or defaults
  const [formData, setFormData] = useState({
    userId: savedData.userId || generateUserId(), // Generate new User ID if not found
    name: savedData.name || "",
    address: savedData.address || "",
    email: savedData.email || "",
    phone: savedData.phone || "",
  });

  useEffect(() => {
    // Store the updated form data in localStorage whenever formData changes
    localStorage.setItem("userData", JSON.stringify(formData));
  }, [formData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Form data saved!");
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        marginBottom: "20px",
      }}
    >
      <h2 style={{ marginBottom: "20px" }}>User Data Form</h2>

      {/* User ID (Auto-Generated) */}
      <label style={labelStyle}>
        User ID:
        <input
          type="text"
          name="userId"
          value={formData.userId}
          readOnly // User can't modify it
          style={{ ...inputStyle, backgroundColor: "#f2f2f2" }}
        />
      </label>

      {/* Name Input */}
      <label style={labelStyle}>
        Name:
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>

      {/* Address Input */}
      <label style={labelStyle}>
        Address:
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>

      {/* Email Input */}
      <label style={labelStyle}>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>

      {/* Phone Input */}
      <label style={labelStyle}>
        Phone:
        <input
          type="text"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>

      {/* Submit Button */}
      <button type="submit" style={buttonStyle}>
        Submit
      </button>
    </form>
  );
};

const labelStyle = {
  display: "block",
  marginBottom: "10px",
  fontWeight: "bold",
};

const inputStyle = {
  display: "block",
  marginBottom: "20px",
  padding: "10px",
  width: "100%",
  borderRadius: "5px",
  border: "1px solid #ccc",
};

const buttonStyle = {
  margin: "5px",
  padding: "10px 20px",
  borderRadius: "5px",
  border: "none",
  backgroundColor: "#28a745",
  color: "#fff",
  cursor: "pointer",
  fontSize: "1rem",
};

export default UserDataForm;
