import React, { useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";

const Counter = () => {
  // Retrieve count from localStorage or default to 0
  const [count, setCount] = useState(() => {
    return Number(localStorage.getItem("counter")) || 0;
  });

  useEffect(() => {
    // Save count to localStorage whenever it updates
    localStorage.setItem("counter", count);
  }, [count]);

  const props = useSpring({
    backgroundColor: `rgba(0, 150, 136, ${Math.min(count / 10, 1)})`,
    config: { duration: 500 },
  });

  return (
    <animated.div
      style={{
        ...props,
        padding: "20px",
        textAlign: "center",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
        marginBottom: "20px",
      }}
    >
      <h1 style={{ fontSize: "3rem", marginBottom: "20px" }}>{count}</h1>
      <button style={buttonStyle} onClick={() => setCount(count + 1)}>
        Increment
      </button>
      <button style={buttonStyle} onClick={() => setCount(count - 1)}>
        Decrement
      </button>
      <button style={buttonStyle} onClick={() => setCount(0)}>
        Reset
      </button>
    </animated.div>
  );
};

const buttonStyle = {
  margin: "5px",
  padding: "10px 20px",
  borderRadius: "5px",
  border: "none",
  backgroundColor: "#007BFF",
  color: "#fff",
  cursor: "pointer",
  fontSize: "1rem",
};

export default Counter;
