import React from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Counter from "./components/Counter";
import UserDataForm from "./components/UserDataForm";
import RichTextEditor from "./components/RichTextEditor";

function App() {
  return (
    <Router>
      <div>
        {/* Navigation links */}
        <nav>
          <Link to="/" style={navLinkStyle}>Counter</Link>
          <Link to="/form" style={navLinkStyle}>User Data Form</Link>
          <Link to="/editor" style={navLinkStyle}>Rich Text Editor</Link>
        </nav>
        
        {/* Define Routes */}
        <Routes>
          <Route path="/" element={<Counter />} />
          <Route path="/form" element={<UserDataForm />} />
          <Route path="/editor" element={<RichTextEditor />} />
        </Routes>
      </div>
    </Router>
  );
}

const navLinkStyle = {
  margin: "0 15px",
  padding: "10px",
  textDecoration: "none",
  color: "#007BFF",
  fontSize: "1.2rem",
};

export default App;
